import "./Goods.css";
import { Link } from "react-router-dom";

const Goods = ({ goods_pic
  , goods_details
  , goods_price, goods_name, goods_category
  ,
  _id }) => {
  return (
    <div className="product">
      <img src={goods_pic} alt={goods_name} />

      <div className="product__info">
        <p className="info__name">{goods_name}</p>

        <p className="info__description">{goods_details.substring(0, 100)}...</p>

        <p className="info__price">${goods_price}</p>

        <Link to={`/product/${_id}`} className="info__button">
          View
        </Link>
      </div>
    </div>
  );
};

export default Goods;
