import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Form, Button } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Loading from "../components/Loading";
import ErrorMessage from "../components/ErrorMessage";
import FormContainer from '../components/FormContainer'
import { listGoodsDetails, updateGoods } from '../actions/goodsActions'
import { GOODS_UPDATE_RESET } from '../constants/productConstants'
import { app } from '../base'

const ProductEditScreen = ({ match, history }) => {
  const productId = match.params.id

  const [name, setName] = useState('')
  const [price, setPrice] = useState(0)
  // const [image, setImage] = useState('')
  // const [brand, setBrand] = useState('')
  const [category, setCategory] = useState('')
  const [countInStock, setCountInStock] = useState(0)
  const [description, setDescription] = useState('')
  const [imageURL, setImageURL] = useState('')

  const dispatch = useDispatch()

  const goodsDetails = useSelector((state) => state.goodsDetails)
  const { loading, error, goods } = goodsDetails

  const goodsUpdate = useSelector((state) => state.goodsUpdate)
  const {
    loading: loadingUpdate,
    error: errorUpdate,
    success: successUpdate,
  } = goodsUpdate

  const onFileChange = async (e) => {
    const file = e.target.files[0]
    const storageRef = app.storage().ref()
    const fileRef = storageRef.child(file.name)
    await fileRef.put(file)
    setImageURL(await fileRef.getDownloadURL())
  }

  useEffect(() => {
    if (successUpdate) {
      dispatch({ type: GOODS_UPDATE_RESET })
      history.push('/admin/productlist')
    } else {
      if (!goods.goods_name || goods._id !== productId) {
        dispatch(listGoodsDetails(productId))
      } else {
        setName(goods.goods_name)
        setPrice(goods.goods_price)
        setImageURL('')
        // setBrand(product.brand)
        setCategory(goods.category)
        setCountInStock(goods.countInStock)
        setDescription(goods.goods_details)
      }
    }
  }, [dispatch, history, goods, productId, successUpdate])

  const submitHandler = (e) => {
    e.preventDefault()
    dispatch(
      updateGoods({
        _id: productId,
        name,
        price,
        imageURL,
        // brand,
        category,
        description,
        countInStock,
      })
    )
  }

  return (
    <>
      <Link to='/admin/productlist' className='btn btn-light my-3'>
        Go Back
      </Link>

      <FormContainer>
        <h1>Edit Product</h1>
        {loadingUpdate && <Loading />}
        {errorUpdate && <ErrorMessage variant='danger'>{errorUpdate}</ErrorMessage>}

        {loading ? (
          <Loading />
        ) : error ? (
          <ErrorMessage variant='danger'>{error}</ErrorMessage>
        ) : (
          <Form onSubmit={submitHandler}>
            <Form.Group controlId='name'>
              <Form.Label>Name</Form.Label>
              <Form.Control
                type='name'
                placeholder='Enter name'
                value={name}
                onChange={(e) => setName(e.target.value)}
              ></Form.Control>
            </Form.Group>

            <Form.Group controlId='price'>
              <Form.Label>price Address</Form.Label>
              <Form.Control
                type='number'
                placeholder='Enter price'
                value={price}
                onChange={(e) => setPrice(e.target.value)}
              ></Form.Control>
            </Form.Group>

            <Form.Group controlId='image'>
              <Form.Label>Image</Form.Label>
              {imageURL === '' ? (
                <input type='file' onChange={onFileChange} />
              ) : (
                <p>Image Selected !!!!</p>
              )}

              {imageURL === '' ? (
                <p>no image</p>
              ) : (
                <img height='100' width='100' src={imageURL} alt={imageURL} />
              )}
            </Form.Group>

            {/* <Form.Group controlId='brand'>
              <Form.Label>Brand</Form.Label>
              <Form.Control
                type='text'
                placeholder='Enter brand'
                value={brand}
                onChange={(e) => setBrand(e.target.value)}
              ></Form.Control>
            </Form.Group> */}

            <Form.Group controlId='countInStock'>
              <Form.Label>Count In Stock</Form.Label>
              <Form.Control
                type='number'
                placeholder='Enter countInStock'
                value={countInStock}
                onChange={(e) => setCountInStock(e.target.value)}
              ></Form.Control>
            </Form.Group>

            <Form.Group controlId='category'>
              <Form.Label>Category</Form.Label>
              <Form.Control
                type='text'
                placeholder='Enter category'
                value={category}
                onChange={(e) => setCategory(e.target.value)}
              ></Form.Control>
            </Form.Group>

            <Form.Group controlId='description'>
              <Form.Label>Details</Form.Label>
              <Form.Control
                type='text'
                placeholder='Enter Details '
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              ></Form.Control>
            </Form.Group>

            <Button type='submit' variant='primary'>
              Update
            </Button>
          </Form>
        )}
      </FormContainer>
    </>
  )
}

export default ProductEditScreen
