import React, { useEffect, useState } from "react";
// import MainScreen from "../../components/MainScreen";
import { Button, Card, Form } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { createGoodsAction } from "../actions/goodsActions";
import Loading from "./Loading";
import ErrorMessage from "./ErrorMessage";
import ReactMarkdown from "react-markdown";

function CreateGoods({ history }) {
  const [goods_name, setGoods_name] = useState("");
  const [goods_details, setGoods_details] = useState("");
  const [goods_category, setGoods_category] = useState("");
  const [goods_pic, setGoods_pic] = useState(
    "https://icon-library.com/images/anonymous-avatar-icon/anonymous-avatar-icon-25.jpg"
  );
  const [picMessage, setPicMessage] = useState(null);

  const dispatch = useDispatch();

  const goodsCreate = useSelector((state) => state.goodsCreate);
  const { loading, error, goods } = goodsCreate;

  console.log(goods);

  const resetHandler = () => {
    setGoods_name("");
    setGoods_details("");
    setGoods_category("");
  };

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(createGoodsAction(goods_name, goods_details, goods_category, goods_pic));
    if (!goods_name || !goods_details || !goods_category) return;

    resetHandler();
    history.push("/");
  };
  const postDetails = (pics) => {
    if (
      pics ===
      "https://icon-library.com/images/anonymous-avatar-icon/anonymous-avatar-icon-25.jpg"
    ) {
      return setPicMessage("Please Select an Image");
    }
    setPicMessage(null);
    if (pics.type === "image/jpeg" || pics.type === "image/png") {
      const data = new FormData();
      data.append("file", pics);
      data.append("upload_preset", "goods_pic");
      data.append("cloud_name", "fyp2022");
      fetch("https://api.cloudinary.com/v1_1/fyp2022/image/upload", {
        method: "post",
        body: data,
      })
        .then((res) => res.json())
        .then((data) => {
          setGoods_pic(data.url.toString());
        })
        .catch((err) => {
          console.log(err);
        });
    } else {
      return setPicMessage("Please Select an Image");
    }
  };

  useEffect(() => {}, []);

  return (
    // <MainScreen title="Create a Note">
      <Card>
        <Card.Header>Add a new Goods</Card.Header>
        <Card.Body>
          <Form onSubmit={submitHandler}>
            {error && <ErrorMessage variant="danger">{error}</ErrorMessage>}
            <Form.Group controlId="title">
              <Form.Label>Goods name</Form.Label>
              <Form.Control
                type="text"
                value={goods_name}
                placeholder="Enter the goods's name"
                onChange={(e) => setGoods_name(e.target.value)}
              />
            </Form.Group>

            <Form.Group >
              <Form.Label>Goods details</Form.Label>
              <Form.Control
                as="textarea"
                value={goods_details}
                placeholder="Enter the goods more details"
                rows={4}
                onChange={(e) => setGoods_details(e.target.value)}
              />
            </Form.Group>
            {content && (
              <Card>
                <Card.Header>Goods details Preview</Card.Header>
                <Card.Body>
                  <ReactMarkdown>{goods_details}</ReactMarkdown>
                </Card.Body>
              </Card>
            )}

            <Form.Group >
              <Form.Label>Goods Category</Form.Label>
              <Form.Control
                type="content"
                value={goods_category}
                placeholder="Enter the Category"
                onChange={(e) => setGoods_category(e.target.value)}
              />
            </Form.Group>

            {picMessage && (
            <ErrorMessage variant="danger">{picMessage}</ErrorMessage>
          )}
          <Form.Group controlId="pic">
            <Form.Label>Profile Picture</Form.Label>
            <Form.File
              onChange={(e) => postDetails(e.target.files[0])}
              id="custom-file"
              type="image/png"
              label="Upload Goods Picture"
              custom
            />
          </Form.Group>

            {loading && <Loading size={50} />}
            <Button type="submit" variant="primary">
              Add a new goods
            </Button>
            <Button className="mx-2" onClick={resetHandler} variant="danger">
              Reset Feilds
            </Button>
          </Form>
        </Card.Body>

        <Card.Footer className="text-muted">
          Creating on - {new Date().toLocaleDateString()}
        </Card.Footer>
      </Card>
    // </MainScreen>
  );
}

export default CreateGoods;
